package week1.day2;

public class FindOddOrEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=13;
		if(n%2==0) {
			System.out.println("The Number " +n+ " is Even");
		}
		else {
			System.out.println("The Number " +n+ " is Odd");
		}

	}

}
